mapper.select:
select id, thumbnail, title, coach_id as coachId, level, type1, type2, type3
from classes
where id=#{id}

mapper.update:
update classes
set thumbnail=#{thumbnail}, title=#{title}, coach_id=#{coachId}, level=#{level}, type1=#{type1}, type2=#{type2}, type3=#{type3}
where id=#{id} and coach_id=#{coachId}
