mapper.select:
SELECT ID, NICKNAME, HEIGHT, WEIGHT, BIRTH_YEAR, GENDER, PROFILE
FROM USERS
WHERE ID=#{id}

mapper.update:
UPDATE USERS
SET PASSWORD=#{password}, NICKNAME=#{nickname}, HEIGHT=#{height}, WEIGHT=#{weight}, PROFILE=#{profile}
WHERE ID=#{id}

mapper.select:
SELECT PASSWORD
FROM USERS
WHERE ID=#{id};
